mapper.insert:
insert into item(
	sku, featurejson, image, retailPrice, promotionPrice,
	promotionTime, wholePrice, minimumQuantity, maximumAcceptQuantity,
	availableQuantity, safeStock
)values(
	#{sku}, #{featurejson}, #{image}, #{retailPrice},#{promotionPrice},
	#{promotionTime},#{wholePrice}, #{minimumQuantity},#{maximumAcceptQuantity},
	#{availableQuantity},#{safeStock}
)
