mapper.insert:
        insert into item(
        	sku, name, nameInChinese, featureJson, image, retailPrice, promotionPrice,
        	promotionTime, wholePrice, minimumQuantity, maximumAcceptQuantity,
        	availableQuantity, safeStock, productId, categoryId, sellerId
        )values(
        	#{sku}, #{name}, #{nameInChinese}, #{featureJson}, #{image}, #{retailPrice},#{promotionPrice},
        	#{promotionTime},#{wholePrice}, #{minimumQuantity},#{maximumAcceptQuantity},
        	#{availableQuantity},#{safeStock}, #{productId}, #{categoryId}, #{sellerId}
        )

	        select @@identity as id
