	receiver, unitIds, createAt, status, orderMoney, 
	translationMoney, storeMoney, serviceMoney, otherMoney, 
	totalGetFromAdmin, totalPayAdmin, total, coinage, beginDate, endDate
) 
values(
	#{receiver}, #{unitIds}, #{createAt}, #{status}, #{orderMoney},
	#{translationMoney}, #{storeMoney}, #{serviceMoney}, #{otherMoney},
	#{totalGetFromAdmin}, #{totalPayAdmin}, #{total}, #{coinage}, #{beginDate}, #{endDate}
)
