mapper.insert:
insert into orderItem(
	itemId, sku, productId, name, coinage, sellerId, num, price, priceRMB, orderId, internationalShippingService, internationalShippingFee, ifeeRMB, internationalPromiseDays, countryCode
)values(
	#{itemId}, #{sku}, #{productId}, #{name}, #{coinage}, #{sellerId}, #{num}, #{price}, #{priceRMB}, #{orderId}, #{internationalShippingService}, #{internationalShippingFee}, #{ifeeRMB}, #{internationalPromiseDays}, #{countryCode}
)
